import React, { useState, useEffect } from 'react';
import './Mousse.css';

function isOverlapping(newDrop, existingDrops) {
  for (const drop of existingDrops) {
    if (
      newDrop.left < drop.left + drop.width &&
      newDrop.left + newDrop.width > drop.left &&
      newDrop.top < drop.top + drop.height &&
      newDrop.top + newDrop.height > drop.top
    ) {
      return true;
    }
  }
  return false;
}

const maxWidth = window.innerWidth;
const maxHeight = window.innerHeight;

function Mousse() {
  const [drops, setDrops] = useState([]);

  useEffect(() => {
    const newDrops = [];
    for (let i = 0; i < 500; i++) {
      let size = Math.random() * (200 - 10) + 50;
      let leftPosition, topPosition;
      do {
        size = Math.random() * (200 - 10) + 50;
        leftPosition = Math.random() * (maxWidth - size);
        topPosition = Math.random() * (maxHeight - size);
      } while (isOverlapping({ left: leftPosition, top: topPosition, width: size, height: size }, newDrops));

      newDrops.push({
        left: leftPosition,
        top: topPosition,
        width: size,
        height: size,
        class: `drop drop${i % 2 === 0 ? 1 : 2}`
      });
    }
    setDrops(newDrops);
  }, []);

  return (
    <div className="c">
      {drops.map((drop, index) => (
        <div
          key={index}
          className={drop.class}
          style={{
            left: drop.left + 'px',
            top: drop.top + 'px',
            width: drop.width + 'px',
            height: drop.height + 'px'
          }}
        ></div>
      ))}
    </div>
  );
}

export default Mousse;
