import React, { useEffect, useRef, useState } from 'react';
import './Mousse.css';

const Mousse = ({ onScrollEnd }) => {
  const [isVisible, setIsVisible] = useState(true);
  const mousseRef = useRef(null);

  c

  useEffect(() => {
    const handleScroll = () => {
      if (mousseRef.current) {
        const rect = mousseRef.current.getBoundingClientRect();
        if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
          setIsVisible(false);
          onScrollEnd();
        }
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [onScrollEnd]);

  return (
    <div className={`mousse-container ${isVisible ? 'visible' : 'invisible'}`} ref={mousseRef}>
      {/* Contenu de la mousse */}
    </div>
  );
};

export default Mousse;
