import React, { useEffect, useState } from 'react';
import './Accueil.css';

const Accueil = () => {
    const [scrollOpacity, setScrollOpacity] = useState(1);

    useEffect(() => {
        const handleScroll = () => {
            const scrollPosition = window.scrollY;
            // Calculez l'opacité en fonction de la position de scroll (ajustez le diviseur pour contrôler la vitesse de l'animation)
            const opacity = 1 - scrollPosition / 1000;
            // Assurez-vous que l'opacité reste dans la plage de 0 à 1
            setScrollOpacity(opacity > 0 ? opacity : 0);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div className="accueil" style={{ opacity: scrollOpacity }}>
            {/* Contenu de votre page d'accueil */}
        </div>
    );
};

export default Accueil;
