import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import './Loading.css';

const Loading = () => {
    const history = useHistory();

    useEffect(() => {
        const timer = setTimeout(() => {
            // Rediriger l'utilisateur vers le composant Acceuil après 2000 millisecondes (2 secondes)
            history.push('/acceuil'); // Assurez-vous que l'URL correspond à votre route
        }, 2000); // Le délai est en millisecondes (2000 millisecondes = 2 secondes)

        // Nettoie le timer si le composant est démonté avant que le délai ne soit écoulé
        return () => clearTimeout(timer);
    }, [history]); // Dépendance vide signifie que useEffect ne s'exécutera qu'une seule fois après le rendu initial

    return (
        <div className='loader'></div>
    );
};

export default Loading;
