import React, { useState, useEffect, useCallback } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import RoutesConfig from '../../Components/RoutesConfig/RoutesConfig';
import '../../Style.css/style.css';
import Loading from '../../Components/Loading/Loading.jsx';

function App() {
    const [isLoading, setIsLoading] = useState(true);

    const handleAnimationEnd = useCallback(() => {
        setIsLoading(false);
    }, []);

    useEffect(() => {
        const timerId = setTimeout(() => {
            setIsLoading(false);
        }, 3000);

        return () => {
            clearTimeout(timerId);
        };
    }, []); // pas de dépendances ici car nous n'utilisons pas de variables externes dans le useEffect

    return (
        <Router>
            {isLoading ? (
                <Loading className={isLoading ? '' : 'fade-out'} onAnimationEnd={handleAnimationEnd} />
            ) : (
                <RoutesConfig />
            )}
        </Router>
    );
}

export default App;
