import React from 'react';
import './Mousse.css';

function isOverlapping(newDrop, existingDrops) {
  for (const drop of existingDrops) {
    const rect1 = newDrop.getBoundingClientRect();
    const rect2 = drop.getBoundingClientRect();
    if (
      rect1.left < rect2.right &&
      rect1.right > rect2.left &&
      rect1.top < rect2.bottom &&
      rect1.bottom > rect2.top
    ) {
      return true;
    }
  }
  return false;
}

const maxWidth = window.innerWidth;
const maxHeight = window.innerHeight;

function Mousse() {
  const numberOfDrops = 500;
  const drops = [];

  for (let i = 0; i < numberOfDrops; i++) {
    let size = Math.random() * (200 - 10) + 50;
    let leftPosition, topPosition;
    do {
      size = Math.random() * (200 - 10) + 50;
      leftPosition = Math.random() * (maxWidth - size) + 'px';
      topPosition = Math.random() * (maxHeight - size) + 'px';
    } while (isOverlapping({ left: leftPosition, top: topPosition, width: size, height: size }, drops));

    const dropClass = `drop drop${i % 2 === 0 ? 1 : 2}`;

    drops.push(
      <div
        key={i}
        className={dropClass}
        style={{
          left: leftPosition,
          top: topPosition,
          width: size + 'px',
          height: size + 'px',
        }}
      ></div>
    );
  }

  return <div className="c">{drops}</div>;
}

export default Mousse;
