import React, { useEffect, useState } from 'react';
import './Mousse.css';

function Mousse() {
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  const numberOfDrops = 500;
  const drops = [];

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const scale = 2; // Facteur d'échelle pour augmenter l'espace entre les gouttes

  for (let i = 0; i < numberOfDrops; i++) {
    const size = Math.random() * (200 - 10) + 50;
    const leftPosition = Math.random() * (windowSize.width - size * scale) + 'px';
    const topPosition = Math.random() * (windowSize.height - size * scale) + 'px';
    const dropClass = `drop drop${i % 2 === 0 ? 1 : 2}`;

    drops.push(
      <div
        key={i}
        className={dropClass}
        style={{
          left: leftPosition,
          top: topPosition,
          width: size + 'px',
          height: size + 'px',
        }}
      ></div>
    );
  }

  return <div className="c">{drops}</div>;
}

export default Mousse;
