import React from 'react';
import './Mousse.css';

function Mousse() {
  const numberOfDrops = 50; // Change this number to adjust the number of drops

  // Array to store drop components with random positions and sizes
  const drops = [];

  // Generate drop components with random positions, sizes, and larger spacing
  for (let i = 0; i < numberOfDrops; i++) {
    // Generate random left and top positions between 0 and 100vw/vh with larger spacing
    const leftPosition = Math.random() * 80 + 10 + 'vw';
    const topPosition = Math.random() * 80 + 10 + 'vh';

    // Generate random size for the drop between 50px and 200px
    const size = Math.random() * (200 - 50) + 50;

    // Alternate between drop1 and drop2 classes based on index
    const dropClass = `drop drop${i % 2 === 0 ? 1 : 2}`;

    // Create drop component with random position, size, and class
    drops.push(
      <div
        key={i}
        className={dropClass}
        style={{
          left: leftPosition,
          top: topPosition,
          width: size + 'px',
          height: size + 'px',
        }}
      ></div>
    );
  }

  return <div className="c">{drops}</div>;
}

export default Mousse;
