import React, { useEffect } from 'react';
import * as THREE from 'three';

const Mousse = () => {
  useEffect(() => {
    // Initialisation de la scène, de la caméra et du rendu
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // Fonction pour créer une bulle de savon
    function createBubble() {
      const geometry = new THREE.SphereGeometry(1, 32, 32);
      const material = new THREE.MeshBasicMaterial({ color: 0xffffff, transparent: true, opacity: 0.5 });
      const bubble = new THREE.Mesh(geometry, material);

      // Positionnement aléatoire de la bulle sur l'écran
      bubble.position.x = (Math.random() - 0.5) * 20;
      bubble.position.y = (Math.random() - 0.5) * 20;
      bubble.position.z = (Math.random() - 0.5) * 20;

      // Taille aléatoire de la bulle
      const scale = Math.random() * 3;
      bubble.scale.set(scale, scale, scale);

      scene.add(bubble);
    }

    // Création de nombreuses bulles de savon
    for (let i = 0; i < 1000; i++) {
      createBubble();
    }

    // Positionnement de la caméra
    camera.position.z = 5;

    // Fonction d'animation
    const animate = () => {
      requestAnimationFrame(animate);

      // Animation des bulles de savon
      scene.children.forEach(bubble => {
        bubble.rotation.x += 0.001;
        bubble.rotation.y += 0.001;
      });

      // Rendu de la scène
      renderer.render(scene, camera);
    };

    // Appel de la fonction d'animation
    animate();

    // Nettoyage de la scène et du rendu lorsque le composant est démonté
    return () => {
      scene.children.forEach(bubble => {
        scene.remove(bubble);
      });
      renderer.dispose();
    };
  }, []); // Le tableau vide indique que ce code ne s'exécutera qu'une seule fois, après le montage initial du composant

  return null;
};

export default Mousse;
