import React, { useEffect, useRef } from 'react';
import './Accueil.css';

const Accueil = () => {
    const imageRef = useRef(null);

    const handleIntersection = (entries) => {
        const [entry] = entries;
        if (entry.isIntersecting) {
            // L'élément est dans la vue, ajustez l'opacité en fonction du ratio d'intersection
            const opacity = 1 - entry.intersectionRatio;
            imageRef.current.style.opacity = opacity;
        }
    };

    useEffect(() => {
        const options = {
            threshold: 0, // L'observateur se déclenche lorsque l'élément est complètement dans la vue
        };

        const observer = new IntersectionObserver(handleIntersection, options);

        if (imageRef.current) {
            observer.observe(imageRef.current);
        }

        return () => {
            if (imageRef.current) {
                observer.unobserve(imageRef.current);
            }
        };
    }, []); // Effect se déclenche une seule fois à l'initialisation

    return (
        <div className="accueil">
            <img ref={imageRef} className="mousse-image" src="../../Assets/foam-texture-background.jpg" alt="Mousse de savon" />
            {/* Contenu de votre page d'accueil */}
        </div>
    );
};

export default Accueil;
