import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Loading.css';

const Loading = () => {
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(true); // Ajoutez un état pour gérer le chargement

    useEffect(() => {
        const timeout = setTimeout(() => {
            setIsLoading(false); // Mettez à jour l'état pour arrêter le chargement
            navigate('/Acceuil');
        }, 3000);

        return () => clearTimeout(timeout);
    }, [navigate]);

    // Conditionnez le rendu du composant en fonction de l'état isLoading
    return (
        <div className={`container ${isLoading ? 'visible' : 'hidden'}`}>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
        </div>
    );
};

export default Loading;
