import React, { useRef, useEffect } from 'react';
import { Canvas, useThree } from 'react-three-fiber';

const Bubbles = () => {
  const { size } = useThree();
  const bubblesRef = useRef();

  useEffect(() => {
    const bubbles = bubblesRef.current.children;

    bubbles.forEach(bubble => {
      const xPos = (Math.random() - 0.5) * size.width * 2;
      const yPos = (Math.random() - 0.5) * size.height * 2;
      const zPos = (Math.random() - 0.5) * 10;

      bubble.position.set(xPos, yPos, zPos);
    });
  }, [size.width, size.height]);

  return (
    <group ref={bubblesRef}>
      {Array.from({ length: 100 }, (_, index) => (
        <mesh key={index}>
          <sphereGeometry args={[0.5, 32, 32]} />
          <meshStandardMaterial color={0x2194ce} transparent opacity={0.8} />
        </mesh>
      ))}
    </group>
  );
};

const Bubble = () => {
  return (
    <div style={{ width: '100%', height: '100vh' }}>
      <Canvas camera={{ position: [0, 0, 0] }}>
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <Bubbles />
      </Canvas>
    </div>
  );
};

export default Bubble;
