import * as THREE from 'three';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader.js';

const scene = new THREE.Scene();
const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
const renderer = new THREE.WebGLRenderer();
renderer.setSize(window.innerWidth, window.innerHeight);
document.body.appendChild(renderer.domElement);

// Chargez votre texture ici
const textureLoader = new THREE.TextureLoader();
const bubbleTexture = textureLoader.load('../../Assets');

// Créez le matériau avec votre texture
const material = new THREE.MeshBasicMaterial({ map: bubbleTexture, transparent: true });

// Créez un grand nombre de bulles de savon
const numberOfBubbles = 100;
const bubbles = [];

for (let i = 0; i < numberOfBubbles; i++) {
    const bubbleGeometry = new THREE.SphereGeometry(0.1, 16, 16);
    const bubbleMesh = new THREE.Mesh(bubbleGeometry, material);

    // Positionnez les bulles de manière aléatoire sur l'écran
    bubbleMesh.position.set(
        Math.random() * 20 - 10,
        Math.random() * 20 - 10,
        Math.random() * 20 - 10
    );

    // Ajoutez les bulles à la scène
    scene.add(bubbleMesh);
    bubbles.push(bubbleMesh);
}

// Animation des bulles
const animate = () => {
    requestAnimationFrame(animate);

    // Faites bouger les bulles (par exemple, vers le haut)
    bubbles.forEach(bubble => {
        bubble.position.y += 0.01; // Vitesse de déplacement vers le haut
        // Réinitialisez la position de la bulle lorsqu'elle sort de l'écran
        if (bubble.position.y > 10) {
            bubble.position.y = -10;
            bubble.position.x = Math.random() * 20 - 10;
            bubble.position.z = Math.random() * 20 - 10;
        }
    });

    renderer.render(scene, camera);
};

// Positionnez et orientez la caméra
camera.position.z = 5;

// Appelez la fonction animate pour commencer l'animation
animate();
