import React, { useEffect, useState } from 'react';
import './Mousse.css'; // Assure-toi que le fichier CSS est correctement importé

const Mousse = () => {
    const [showMousse, setShowMousse] = useState(true);

    useEffect(() => {
        const handleScroll = () => {
            const scrollPosition = window.scrollY;
            if (scrollPosition > 0) {
                setShowMousse(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div className={`mousse-animation ${showMousse ? 'visible' : 'invisible'}`}>
            {/* Contenu de la mousse (si nécessaire) */}
        </div>
    );
};

export default Mousse;
