import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Ajoutez l'import de useNavigate
import './Loading.css';

const Loading = () => {
    const navigate = useNavigate(); // Utilisez useNavigate pour la navigation

    useEffect(() => {
        // Utilisez setTimeout pour rediriger après 3 secondes (3000 millisecondes)
        const timeout = setTimeout(() => {
            navigate('/Acceuil'); // Utilisez navigate pour rediriger vers le chemin '/Acceuil'
        }, 3000);

        // Assurez-vous de nettoyer le timeout lors du démontage du composant
        return () => clearTimeout(timeout);
    }, [navigate]); // Ajoutez navigate comme dépendance

    return (
        <div className="container">
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
        </div>
    );
};

export default Loading;
