import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import RoutesConfig from '../../Components/RoutesConfig/RoutesConfig';
import '../../Style.css/style.css';

function App() {
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsLoading(false);
        }, 3000);

        return () => clearTimeout(timer);
    }, []);

    const loadingClasses = isLoading ? 'container' : 'container fade-out'; // Déterminez les classes CSS en fonction de l'état de chargement

    return (
        <Router>
            <div className={loadingClasses}>
                <div className="dot"></div>
                <div className="dot"></div>
                <div className="dot"></div>
            </div>
            {!isLoading && <RoutesConfig />} {/* Affichez RoutesConfig une fois que le chargement est terminé */}
        </Router>
    );
}

export default App;
