import React, { useEffect } from 'react';
import './Accueil.css'; // Importez le fichier CSS

const Accueil = () => {
    useEffect(() => {
        const handleScroll = () => {
            const scrollPosition = window.scrollY;
            const mousseElement = document.querySelector('.accueil');
            if (mousseElement) {
                const opacity = 1 - scrollPosition / 1000; // Ajustez le diviseur pour contrôler la vitesse de l'animation
                mousseElement.style.opacity = opacity > 0 ? opacity : 0;
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div className="accueil">
            {/* Contenu de votre page d'accueil */}
        </div>
    );
};

export default Accueil;
