import React, { useEffect, useState } from 'react';
import './Accueil.css';

const Accueil = () => {
    const [scrollPosition, setScrollPosition] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
            const position = window.scrollY;
            setScrollPosition(position);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const maskHeight = Math.min(100, scrollPosition); // Limitez la hauteur du masque à 100px ou à la position de scroll, selon la plus petite valeur

    const maskStyle = {
        WebkitMaskImage: `linear-gradient(to bottom, transparent ${maskHeight}px, black 100%)`,
        maskImage: `linear-gradient(to bottom, transparent ${maskHeight}px, black 100%)`
    };

    return (
        <div className="accueil" style={maskStyle}>
            {/* Contenu de votre page d'accueil */}
        </div>
    );
};

export default Accueil;
