import React, { useEffect } from 'react';
import p5 from 'p5';

const Mousse = () => {
  useEffect(() => {
    const sketch = (p) => {
      p.setup = () => {
        p.createCanvas(window.innerWidth, window.innerHeight);
        p.background(255);
        p.strokeWeight(5);

        for (let i = 0; i < 400; i++) {
          let x = p.random(p.width);
          let y = p.random(p.height);
          let diameter = p.random(5, 75);
          let a = diameter / 5;
          p.stroke(p.random(0, 255), p.random(0, 255), p.random(0, 255), 15);
          p.fill(218, 240, 253, 100);
          p.ellipse(x, y, diameter, diameter);
          p.noStroke();
          p.fill(255, 255, 255);
          p.ellipse(x + a, y + a, a, a);
        }
      };

      p.draw = () => {
        // Code de dessin (s'il y a lieu) ici
        // ...
      };
    };

    new p5(sketch);
  }, []);

  return <div id="canvas-container" style={{ width: '100vw', height: '100vh', position: 'fixed', top: 0, left: 0 }}></div>;
};

export default Mousse;
