import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import RoutesConfig from '../../Components/RoutesConfig/RoutesConfig';
import '../../Style.css/style.css';

function App() {
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Simuler une requête asynchrone qui prend 3 secondes pour se charger
        const timer = setTimeout(() => {
            setIsLoading(false);
        }, 3000);

        // Nettoyer le timer si le composant est démonté avant que le délai ne soit écoulé
        return () => clearTimeout(timer);
    }, []);

    return (
        <Router>
            {isLoading ? (
                // Afficher le composant Loading tant que isLoading est vrai
                <Loading />
            ) : (
                // Une fois que isLoading devient faux, afficher le composant Acceuil
                <RoutesConfig />
            )}
        </Router>
    );
}

export default App;
