import { useEffect } from 'react';

const Mousse = () => {
  useEffect(() => {
    // Charger le script du viewer
    const script = document.createElement('script');
    script.type = 'module';
    script.src = 'https://unpkg.com/@splinetool/viewer@0.9.490/build/spline-viewer.js';
    document.body.appendChild(script);

    // Attendre que le script soit chargé avant d'initialiser le viewer
    script.onload = () => {
      // Initialiser le viewer
      const viewer = document.createElement('spline-viewer');
      viewer.setAttribute('url', 'https://prod.spline.design/l3okS7J9tAsx5H4H/scene.splinecode');

      // Dupliquer et ajouter le viewer à l'écran plusieurs fois
      const numInstances = 4; // Vous pouvez ajuster le nombre d'instances selon vos besoins
      for (let i = 0; i < numInstances; i++) {
        const clonedViewer = viewer.cloneNode(true);
        document.body.appendChild(clonedViewer);
      }
    };

    return () => {
      // Nettoyer le script lors du démontage du composant
      document.body.removeChild(script);
    };
  }, []); // Utilisation d'un tableau vide comme dépendance pour s'assurer que cela ne s'exécute qu'une seule fois

  return null; // Ce composant ne rend rien puisque tout est géré dans useEffect
};

export default Mousse;
