import React, { useEffect } from "react";
import "./Mousse.css"; // Assurez-vous d'importer votre fichier CSS

const Mousse = () => {
  useEffect(() => {
    const numDrops = 300; // Nombre de gouttes que vous souhaitez afficher

    const container = document.createElement("div");
    container.className = "container";

    for (let i = 0; i < numDrops; i++) {
      const drop = document.createElement("div");
      drop.className = "drop";

      // Génération de tailles aléatoires
      const size = Math.floor(Math.random() * 150) + 50; // Taille entre 50px et 200px
      drop.style.width = `${size}px`;
      drop.style.height = `${size}px`;

      // Génération de positions aléatoires
      const x = Math.random() * window.innerWidth;
      const y = Math.random() * (window.innerHeight - size); // Soustrayez la hauteur pour éviter l'espace blanc en haut
      drop.style.left = `${x}px`;
      drop.style.top = `${y}px`;

      container.appendChild(drop);
    }

    document.body.appendChild(container);
  }, []); // Le tableau vide [] garantit que useEffect est exécuté une seule fois après le rendu initial

  return <></>; // Vous pouvez retourner n'importe quoi ou laisser vide selon vos besoins
};

export default Mousse;
