import React from 'react';
import './Mousse.css';

function Mousse() {
  const numberOfDrops = 100; // Change this number to adjust the number of drops

  // Array to store drop components with random positions
  const drops = [];

  // Generate drop components with random positions
  for (let i = 0; i < numberOfDrops; i++) {
    // Generate random left and top positions between 0 and 100vw/vh (viewport width/height)
    const leftPosition = Math.random() * 100 + 'vw';
    const topPosition = Math.random() * 100 + 'vh';

    // Alternate between drop1 and drop2 classes based on index
    const dropClass = `drop drop${i % 2 === 0 ? 1 : 2}`;

    // Create drop component with random position and class
    drops.push(
      <div key={i} className={dropClass} style={{ left: leftPosition, top: topPosition }}></div>
    );
  }

  return <div className="c">{drops}</div>;
}

export default Mousse;
