import React, { useRef, useMemo } from 'react';
import { Canvas } from 'react-three-fiber';
import { SphereBufferGeometry } from 'three';

const Bubble = () => {
  const mesh = useRef();

  const radius = useMemo(() => Math.random() * 0.5 + 0.1, []);

  // Créez une nouvelle instance de SphereBufferGeometry
  const geometry = useMemo(() => new SphereBufferGeometry(radius, 16, 16), [radius]);

  return (
    <mesh ref={mesh} position={[Math.random() * 10 - 5, Math.random() * 10 - 5, Math.random() * 10 - 5]} geometry={geometry}>
      <meshStandardMaterial color={0xadd8e6} transparent opacity={0.8} />
    </mesh>
  );
};

const Mousse = () => {
  return (
    <div style={{ width: '100%', height: '100%' }}>
      <Canvas camera={{ position: [0, 0, 5] }}>
        {[...Array(100)].map((_, index) => (
          <Bubble key={index} />
        ))}
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
      </Canvas>
    </div>
  );
};

export default Mousse;
