import React from 'react';
import './Mousse.css';

function Mousse() {
  const numberOfDrops = 1000; 
  const drops = [];

  for (let i = 0; i < numberOfDrops; i++) {
    const size = Math.random() * (200 - 50) + 50;
    const leftPosition = Math.random() * (window.innerWidth * 1.5 - size) + 'px';
    const topPosition = Math.random() * (window.innerHeight * 1.5 - size) + 'px';
    const dropClass = `drop drop${i % 2 === 0 ? 1 : 2}`;

    drops.push(
      <div
        key={i}
        className={dropClass}
        style={{
          left: leftPosition,
          top: topPosition,
          width: size + 'px',
          height: size + 'px',
        }}
      ></div>
    );
  }

  return <div className="c">{drops}</div>;
}

export default Mousse;
