import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom'; // Importez useHistory depuis react-router-dom
import './Loading.css';

const Loading = () => {
    const history = useHistory(); // Initialisez l'objet history

    useEffect(() => {
        // Utilisez setTimeout pour rediriger après 5 secondes (5000 millisecondes)
        const timeout = setTimeout(() => {
            history.push('/accueil'); // Redirigez vers le composant Accueil
        }, 5000);

        // Assurez-vous de nettoyer le timeout lors du démontage du composant
        return () => clearTimeout(timeout);
    }, [history]);

    return (
        <div className="container">
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
        </div>
    );
};

export default Loading;
