import React, { useEffect, useRef, useState } from 'react';
import './Mousse.css'; // Assure-toi d'avoir un fichier de style pour le composant

const Mousse = ({ onScrollEnd }) => {
  const [isVisible, setIsVisible] = useState(true);
  const mousseRef = useRef(null);

  const handleScroll = () => {
    if (mousseRef.current) {
      const rect = mousseRef.current.getBoundingClientRect();
      if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
        // La mousse est visible à l'écran
        setIsVisible(false);
        onScrollEnd();
      }
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className={`mousse-container ${isVisible ? 'visible' : 'invisible'}`} ref={mousseRef}>
      {/* Contenu de la mousse */}
    </div>
  );
};

export default Mousse;
